#include<iostream>
#include<math.h>
using namespace std;

bool isPrime(int num){
	if(num < 2)
		return false;
	else{
		float max = sqrt(num);
		for (int i=2;i <= max;i++){
			if(num % i == 0)
				return false;
		}
		return true;
	}
}

int main(){
	int primes = 0;
	for(int i=2;i<8000000;i++){
		if(isPrime(i))
			primes++;
	}
	cout<<primes<<endl;
	return 0;
}